import React, {Component}  from 'react';
import Cell from '../components/Cell';

class Board extends Component {
  constructor(props){
    super(props);
    this.state = {
      cells: Array(9).fill(null),
      xNextPlayer: true,
    };
}

  handleClick(i) {
    const cells = this.state.cells.slice();
    cells[i] = this.state.xNextPlayer ? 'X' : 'O';
    this.setState({ 
      cells: cells,
      xNextPlayer: !this.state.xNextPlayer,
    });

}

  renderCell(i) {
    return (
       <Cell value={this.state.cells[i]} onClick={() => this.handleClick(i)}/>

    );
  }
  render(){
      const status = 'Next player: X';
      return(
          <div>
        <div className="status">{status}</div>
        <div className="board-row">
          {this.renderCell(0)}
          {this.renderCell(1)}
          {this.renderCell(2)}
        </div>
        <div className="board-row">
          {this.renderCell(3)}
          {this.renderCell(4)}
          {this.renderCell(5)}
        </div>
        <div className="board-row">
          {this.renderCell(6)}
          {this.renderCell(7)}
          {this.renderCell(8)}
        </div>
      </div>
    );
  }
}

export default Board;
