//
import Header from '../components/Header.js';
import Board from '../components/Board.js';
import img0 from '../images/0.png';
import img1 from '../images/1.png';
import Cell from './Cell.js';
import Info from './Info.js';
var t=0;



class Game extends Component {
  // if( this.state.gamestae)
  state={
    visible:false, buttonStart:false
};

  render(){

   var start= <button onClick={()=>{this.setState({buttonStart:true}); }} className='btn btn-dark niceFont buttonCentered'>Start Game</button>;
   var names=<div className="makeFlex"><img src={img0} alt="missed" className="icon left" style={{display : "block" }}></img><img src={img1} alt="missed" className='icon right' style={{display : "block" }}></img></div>;
   var resetAndNew =<div><button className="left">Restart </button> <button className="right">New Game </button>   </div>
    return (
  <div className='container'>
   {!this.state.visible && !this.state. buttonStart ? <Header/>: null }
   {this.state.visible && !this.state. buttonStart ?<><Header/><Info/> <>{start}</></>: null }
   {this.state. buttonStart ?<><Header/> <>{names}</><Board/> </>: null }
  <button className='btn btn-dark niceFont buttonCentered' style={{display : "block" }} onClick={()=>{this.setState({visible:true}); }}>Start</button>

    {/* the game started ? <Board/> : null */}
    {/* <Cell/> */}
    {/* <Info/> */}
  </div>
);
  }
}

export default Game;
