var p2= this.ref.name2.value;
class Info extends Component{

  render(){
    return(
<div className='center'>
    <div className='info   d-flex flex-row'>
        <div className='player1 mr-3'>
          <div className='name'>
            Name:
            <img src={Image1} className='icon' alt="Player1 icon"></img>
          </div>
          <div >
            <input ref="name1" type="text" className="form-control" ></input>
          </div>
        </div>
        <div className='player2 ml-3'>
          <div className='name'>
            Name:
            <img src={Image2} className='icon' alt="Player2 icon"></img>
          </div>
          <div >
           <input ref="name2" type="text" className="imput form-control"></input>
          </div>
        </div>
    </div>
 {/* <button onClick={refreshPage} className='mt-3'>Start Game</button> */}
</div>
)
  }
}


export default Info ;
