import React, {Component}  from 'react';
import img0 from '../images/0.png';
import img1 from '../images/1.png';
import Board from '../components/Board.js';
import Header from '../components/Header.js'    


var t= img0;
var r=0;
class  Cell extends Component {
 constructor() {
        super()
        this.state = {
            answer: img0
        }
    }
 handleClick = (e) => {
    e.preventDefault();

    console.log("Hi there, user!");
  };
//
//
state = {
     isActive:false
  }

  handleClick = (e) => {

      this.setState({
          isActive: true
      })
  }


//

render(){
     if(r%2===0){
      t=img0;
}else{
  t=img1;
}
r++;
    if (this.state.isActive) {
      return(
          <div className=''>

      <div className='row together' >
                    <div onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={this.state.answer} alt="missed" className='square' style={{display : "block" }}></img>
                     </>  
                    </div>
                    <div  onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={t} alt="missed" className='square' style={{display : "block" }} ></img>
                     </>  
                    </div>
                    <div  onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={t} alt="missed" className='square' style={{display : "block" }} ></img>
                     </>  
                    </div>
                </div>
            {/* player x ? X : O */}
            {/* <Board/> */}
          </div>
        );   } else{
          return(
          <div className=''>

      <div className='row together' >
                    <div onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={t} alt="missed" className='square' style={{display : "none" }}></img>
                     </>  
                    </div>
                    <div  onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={t} alt="missed" className='square' style={{display : "none" }} ></img>
                     </>  
                    </div>
                    <div  onClick={this.handleClick}  className=' border border-dark rounded square'>
                     <>
                        <img src={t} alt="missed" className='square' style={{display : "none" }}></img>
                     </>  
                    </div>
                </div>
            {/* player x ? X : O */}
            {/* <Board/> */}
          </div>
        ); 

}

  }   

}

export default Cell;
